import React, { Fragment } from "react";

interface IThumbProps {
  title: string;
  imageSrc: string;
}

const Thumb: React.FC<IThumbProps> = ({ title, imageSrc }) => (
  <div>
    <img src={imageSrc} />
    <h2>{title}</h2>
    <style jsx global>{`
      body {
        margin: 0;
        padding: 0;
        font-family: "Libre Baskerville, serif";
        font-size: 15px;
        background: #c5c4c4;
      }
    `}</style>
  </div>
);

export default Thumb;
