import React, { Fragment } from "react";

interface IThumbProps {
  title: string;
  imageSrc: string;
}

const Thumb: React.FC<IThumbProps> = ({ title, imageSrc }) => (
  <div>
    <img src={imageSrc} />
    <h2>{title}</h2>
    <style jsx global>{`
      div {
        display: flex;
        justify-content: center;
        align-items: center;
      }
    `}</style>
  </div>
);

export default Thumb;
