import React, { Fragment } from "react";

interface IThumbProps {
  title: string;
  imageSrc: string;
}

const Thumb: React.FC<IThumbProps> = ({ title, imageSrc }) => (
  <div>
    <img src={imageSrc} />
    <h2>{title}</h2>
    <style jsx>{`
      div {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        padding: 2em;
        background: #efefef;
        margin-bottom: 2em;
        box-shadow: 0px 2px 1px -1px rgba(0, 0, 0, 0.2),
          0px 1px 1px 0px rgba(0, 0, 0, 0.14),
          0px 1px 3px 0px rgba(0, 0, 0, 0.12);
      }
      img {
        -webkit-box-shadow: 2px 2px 44px 0px rgba(0, 0, 0, 0.75);
        -moz-box-shadow: 2px 2px 44px 0px rgba(0, 0, 0, 0.75);
        box-shadow: 2px 2px 44px 0px rgba(0, 0, 0, 0.75);
      }
    `}</style>
  </div>
);

export default Thumb;
